def get_skill_span(name, svg_content):
    return f"""                    <span class="skill-chip">
                        {svg_content} {name}
                    </span>"""

def main():
    file_path = r'c:\Users\INTRO\OneDrive\Desktop\Mi CV\index.html'
    
    with open(file_path, 'r', encoding='utf-8') as f:
        content = f.read()

    # Define the order and names as they appear in the HTML (or should appear)
    # Note: The keys in 'svgs' dict should match the keys used here
    skills = [
        ('HTML5', 'html'),
        ('CSS3 / Flex / Grid', 'css'),
        ('JavaScript (ES6+)', 'javascript'),
        ('React', 'react'),
        ('Node.js', 'node'),
        ('Express', 'express'),
        ('REST APIs', 'rest'),
        ('MySQL', 'mysql'),
        ('Git / GitHub', 'git'),
        ('Scrum / Kanban', 'scrum'),
        ('Docker', 'docker'),
        ('JSON', 'json'),
        ('WhatsApp API', 'whatsapp')
    ]

    # Construct the new inner HTML for marquee-track
    new_track_content = '\n                    <!-- Original List -->\n'
    
    for name, key in skills:
        if key in svgs:
            new_track_content += get_skill_span(name, svgs[key]) + '\n'
        else:
            # If we don't have a new SVG, try to keep the old one or use a placeholder?
            # For now, let's assume we will populate all provided ones.
            # If JSON is missing from provided list, we might need to handle it.
            # I'll check if JSON is in the provided list. It wasn't explicitly provided in the last prompt.
            # I will try to extract the existing JSON svg if possible, or just skip it for now and fix later.
            print(f"Warning: No SVG found for {key}")
            pass

    new_track_content += '\n                    <!-- Duplicate List for Infinite Loop -->\n'
    for name, key in skills:
        if key in svgs:
            new_track_content += get_skill_span(name, svgs[key]) + '\n'

    # Regex to replace the content inside <div class="marquee-track"> ... </div>
    # We match non-greedy until the closing div
    pattern = re.compile(r'(<div class="marquee-track">)(.*?)(</div>)', re.DOTALL)
    
    if pattern.search(content):
        new_content = pattern.sub(r'\1\n' + new_track_content + r'                \3', content)
        
        with open(file_path, 'w', encoding='utf-8') as f:
            f.write(new_content)
        print("Successfully updated index.html")
    else:
        print("Error: Could not find marquee-track div")

if __name__ == "__main__":
    main()
